// emailSender.ts

import { m } from "framer-motion";
import { toast } from "./components/ui/use-toast";
// Remove the import statement or replace it with the correct import statement based on the available members in the 'data-uri-to-buffer' module.

export interface EmailOptions {
    from: string;
    to: string;
    subject: string;
    text?: string;
    html?: string;
    attachments?: Array<{
      filename?: string;
      path: string;
      cid?: string;
    }>;
  }


  const logoDatauri = ''
  
  const config: any = await window.api.getConfig();
  export const sendEmail = async (options: EmailOptions) => {
    const finalAttachment = [...(options.attachments ?? []), {filename: 'logo.png',content: logoDatauri,encoding: 'base64', cid: 'logo' }];

    const mailOptions = {
      from: config.emailFrom,
      to: options.to,
      subject: options.subject,
      text: options.text || '',
      html: options.html || '',
      attachments: finalAttachment,
    };
  
    try {
      const result: any = await window.api.sendEmail(mailOptions);
      if (result.success) {
        console.log('Email sent successfully:', result);
        toast({
            title: 'Email sent sucessfuly',
            description: `email sent successfully to ${options.to}`,
          });
      } else {
        toast({
          title: 'Email failed',
          description: `Failed to send email to ${options.to}`,
          variant: 'destructive',
        });
      }
    } catch (error) {
      console.error('Failed to send email:', error);
    }
  };
  